package operatorsinjava;

public class ShiftOperation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 11;
		System.out.println("Left Shift : "+(x<<1)); //doubles the number
		System.out.println("Right Shift : "+(x>>1));//half the number
		
		int y = 50;
		System.out.println("Left Shift : "+(y<<2));//doubles twice
		System.out.println("Right Shift : "+(y>>2));//half twice
		
		
	}

}
